import*as device from"./device.min.js";export async function initialize(){setDeviceClass()}function setDeviceClass(){device.determineInputType().then(e=>{const s=document.body;s.classList.forEach(e=>{e.startsWith("device-")&&s.classList.remove(e)}),s.classList.add(`device-${e}`)}).catch(e=>{document.body.classList.add("device-pointer")})}